function Midway(){
	var $centerHorizontal = $('.centerHorizontal'),
		$centerVertical = $('.centerVertical');

	$centerHorizontal.each(function(){
		$(this).css('marginLeft', -$(this).outerWidth()/2);
	});
	$centerVertical.each(function(){
		$(this).css('marginTop', -$(this).outerHeight()/2);
	});
    if(overop == 1){
	$centerHorizontal.not('#over').css({
		'display' : 'inline',
		'position' : 'absolute',
		'left' : '50%'
	});
	$centerVertical.not('#over').css({
		'display' : 'inline',
		'position' : 'absolute',
		'top' : '50%',
	});
    } else {
     
        $centerHorizontal.css({
		'display' : 'inline',
		'position' : 'absolute',
		'left' : '50%'
	});
	$centerVertical.css({
		'display' : 'inline',
		'position' : 'absolute',
		'top' : '50%',
	});
        
    }
}
$(window).on('load', Midway);
$(window).on('resize', Midway);