function Midway() {
	$('.centerHorizontal').css('marginLeft', - + $('.centerHorizontal').width()/2);
	$('.centerVertical').css('marginTop', - + $('.centerVertical').height()/2);

    $("#infopbut").css({
		'position' : 'absolute',
		'top' : '50%',
	});
    if(overop == 1){
    $(".centerHorizontal").not('#over').css({
		'display' : 'inline',
		'position' : 'absolute',
		'left' : '50%'
	});

	$(".centerVertical").not('#infopbut').not('#over').css({
		'display' : 'inline',
		'position' : 'absolute',
		'top' : '50%',
	});
    $("#over").css({
		'display' : 'none'
	});
} else {
    
  $(".centerHorizontal").css({
		'display' : 'inline',
		'position' : 'absolute',
		'left' : '50%'
	});

	$(".centerVertical").not('#infopbut').css({
		'display' : 'inline',
		'position' : 'absolute',
		'top' : '50%',
	});
    
}

}
$(window).on('load', Midway);
$(window).on('resize', Midway);
